/*-------------------BETTER WAY-------------------------*/
/*-----------------OPTIMISATIONS-------------------------*/
/*

--Avoid Full Sorting
    Sorting the entire array just to find the median wastes computation.
    Instead, we can use a selection algorithm like Quickselect,
    which finds the kth smallest element inO(n) average time.
    This eliminates the need for ð‘‚(ð‘›logð‘›) sorting.

--Precompute and Use Efficient Loops
    Optimize the loops for computing operations
    by avoiding repeated comparisons and using accumulated sums.
*/

// Helper function for Quickselect
int partition(int *nums, int left, int right)
{
    int pivot = nums[right];
    int i = left;
    for (int j = left; j < right; j++)
    {
        if (nums[j] <= pivot)
        {
            int temp = nums[i];
            nums[i] = nums[j];
            nums[j] = temp;
            i++;
        }
    }
    int temp = nums[i];
    nums[i] = nums[right];
    nums[right] = temp;
    return i;
}

int quickSelect(int *nums, int left, int right, int k)
{
    if (left == right)
        return nums[left];
    int pivotIndex = partition(nums, left, right);
    if (pivotIndex == k)
        return nums[k];
    else if (k < pivotIndex)
        return quickSelect(nums, left, pivotIndex - 1, k);
    else
        return quickSelect(nums, pivotIndex + 1, right, k);
}

long long minOperationsToMakeMedianK(int *nums, int numsSize, int k)
{
    long long operations = 0;

    // Find the median using Quickselect
    int mid = numsSize / 2;
    int median = quickSelect(nums, 0, numsSize - 1, mid);

    // Calculate the operations for the median
    operations = abs(median - k);

    // Compute operations for elements on the left of the median
    for (int i = 0; i < mid; i++)
    {
        if (nums[i] > k)
        {
            operations += nums[i] - k;
        }
    }

    // Compute operations for elements on the right of the median
    for (int i = mid + 1; i < numsSize; i++)
    {
        if (nums[i] < k)
        {
            operations += k - nums[i];
        }
    }

    return operations;
}

